type HTTPClient struct {
    timeout time.Duration
    retries int
    baseURL string
}

type Option func(*HTTPClient)

func WithTimeout(d time.Duration) Option {
    return func(c *HTTPClient) { c.timeout = d }
}

func WithRetries(n int) Option {
    return func(c *HTTPClient) { c.retries = n }
}

func WithBaseURL(url string) Option {
    return func(c *HTTPClient) { c.baseURL = url }
}

func NewHTTPClient(opts ...Option) *HTTPClient {
    c := &HTTPClient{timeout: 30 * time.Second}
    for _, opt := range opts {
        opt(c)
    }
    return c
}

type Logger interface {
    Log(msg string)
}

func NewLogger(logType string) Logger {
    switch logType {
    case "console":
        return &ConsoleLogger{}
    case "file":
        return &FileLogger{file: "app.log"}
    case "null":
        return &NullLogger{}
    default:
        return &ConsoleLogger{}
    }
}

type Query struct {
    table   string
    cols    []string
    wheres  []string
    limit   int
}

func NewQuery(table string) *Query {
    return &Query{table: table, cols: []string{"*"}}
}

func (q *Query) Select(cols ...string) *Query {
    q.cols = cols
    return q
}

func (q *Query) Where(cond string, args ...any) *Query {
    q.wheres = append(q.wheres, fmt.Sprintf(cond, args...))
    return q
}

func (q *Query) Limit(n int) *Query {
    q.limit = n
    return q
}

func (q *Query) String() string {
    sql := fmt.Sprintf("SELECT %s FROM %s", 
        strings.Join(q.cols, ", "), q.table)
    if len(q.wheres) > 0 {
        sql += " WHERE " + strings.Join(q.wheres, " AND ")
    }
    if q.limit > 0 {
        sql += fmt.Sprintf(" LIMIT %d", q.limit)
    }
    return sql
}
