func main() {
    if len(os.Args) < 2 {
        fmt.Fprintln(os.Stderr, "usage: which command")
        os.Exit(1)
    }

    path, err := exec.LookPath(os.Args[1])
    if err != nil {
        os.Exit(1)
    }
    fmt.Println(path)
}

